package week1.sat;

public class Prime {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
         int n=5;
         
         for(int i=2;i<n;i++){
         if(n%i==0){
        	 System.out.println("Not a Prime Number");
         } else {
        		 System.out.println("Prime Number");
         }
        	
         
         }
	}

}
